import React, { useEffect } from "react";
import Pins from "../../data/map.json";
import mapboxgl from "mapbox-gl";
import ReactDOM from "react-dom";
import Landmark from "../Landmark";
import PopupContainer from "../PopupContainer";

const Landmarks = ({ map }) => {
  useEffect(() => {
    if (map) {
      Pins.features.forEach((pin, index) => {
        const coord = [
          pin.geometry.coordinates[0],
          pin.geometry.coordinates[1],
        ];
        const popup = new mapboxgl.Popup();
        const popupNode = document.createElement("div");
        const ref = React.createRef;
        ref.current = document.createElement("div");

        const handleClick = () => {
          ReactDOM.render(<PopupContainer />, popupNode);

          console.log("cloiked");

          popup.setLngLat(coord).setDOMContent(popupNode).addTo(map);
        };

        ReactDOM.render(
          <Landmark onMouseEnter={handleClick} map={map} pin={pin} />,
          ref.current
        );

        new mapboxgl.Marker(ref.current)
          .setLngLat(pin.geometry.coordinates)
          .addTo(map);
      });
    }
  }, [map]); // eslint-disable-line react-hooks/exhaustive-deps

  return <div></div>;
};

export default Landmarks;

