import styled from "styled-components";

export const HeaderContainer = styled.header`
  position: absolute;
  top: 2rem;
  left: 2rem;
  right: 2rem;
  z-index: 4;
  text-transform: uppercase;
  line-height: 1;
  color: #fff;
  pointer-events: none;
  margin-left: 0;

  h1,
  h2 {
    text-shadow: 0 0 15px rgba(0, 0, 0, 1);
    cursor: pointer;
  }

  @media (max-width: 500px) {
    text-align: center;
    top: 1rem;
    h1,
    h2 {
      font-size: 1rem;
      display: inline-block;
      margin: 0 0.5rem;
    }
  }
`;

export const H1 = styled.h1`
  font-size: clamp(1.5rem, 6vw, 10rem);
  letter-spacing: 1px;
  cursor: pointer;
`;

export const H2 = styled.h2`
  font-size: clamp(1rem, 3vw, 6rem);
  font-weight: 400;
`;
