import styled from "styled-components";

export const ModalBG = styled.div`
  position: absolute;
  background: rgba(0, 0, 0, 0.9);
  top: 0;
  left: 0;

  height: calc(100vh - 10px);
  width: calc(100vw - 10px);
  color: #fff;
  pointer-events: all;
  z-index: 100;
  display: grid;
  place-items: center;
`;

export const Img = styled.img`
  max-width: 90vw;
  max-height: 90vh;
  position: absolute;
  width: auto;
  height: auto;
  z-index: 101;
  pointer-events: none;
`;
