import React, { useEffect } from "react";
import Pins from "../../data/map.json";
import mapboxgl from "mapbox-gl";
import "./styles.css";
import ReactDOM from "react-dom";

import Landmark from "../Landmark";

const Landmarks = ({ map }) => {
  const handlePopUp = () => {
    console.log("handle");
  };

  useEffect(() => {
    if (map) {
      Pins.features.forEach((pin, index) => {
        const coordinates = pin.geometry.coordinates;
        // const el = document.createElement("div");
        // el.id = "marker_" + index;
        // el.className = "pin-marker";

        // create React reference
        const ref = React.createRef();
        // Create a new DOM node and save it to the React Ref
        ref.current = document.createElement("div");
        // render a Marker Component on our new DOM nodelib
        ReactDOM.render(
          <Landmark onClick={handlePopUp}>âšª</Landmark>,
          ref.current
        );

        const marker = new mapboxgl.Marker(ref.current);

        marker.setLngLat(coordinates);
        marker.addTo(map);
      });
    }
  }, [map]);

  return <div></div>;
};

export default Landmarks;
