import React, { useState } from "react";
import Sidebar from "./Sidebar";
import projects from "../data/landlines.json";

const Unsilenced = ({ map }) => {
  const [open, setOpen] = useState(true);

  const state = projects.features[12].properties;

  if (map && open === true) {
    map.flyTo({
      center: [-105.43647766113281, 37.41816326969145],
      zoom: 12,
      speed: 1,
      padding: { left: 500 },
    });
  }

  const onExit = () => {
    setOpen(false);
    if (map) {
      map.flyTo({
        center: [-106.1828, 37.4792],
        zoom: 8.5,
        speed: 1,
        padding: { left: 0 },
      });
    }
  };

  return (
    <div>
      {open === true ? <Sidebar state={state} map={map} onExit={onExit} /> : ""}
    </div>
  );
};

export default Unsilenced;
