import React from "react";
import * as Yup from "yup";
import { Formik, Form, Field, useField } from "formik";

const InputComponent = ({ label, ...props }) => {
  return (
    <lable>
      <input></input>
    </lable>
  );
};

const FormComponent = ({ handleSuccess }) => {
  const schema = Yup.object().shape({
    firstName: Yup.string().required("Required Field"),
    lastName: Yup.string().required("Required Field"),
    email: Yup.string()
      .email("Must be a valid e-mail address")
      .required("Required Field"),
  });

  return (
    <Formik
      initialValues={{
        firstName: "",
        lastName: "",
        email: "",
      }}
      onSubmit={handleSuccess}
      validationSchema={schema}
    >
      {({ errors, touched }) => (
        <Form>
          <InputComponent name="firstname" type="text" label="First Name" />
          <InputComponent name="lastname" type="text" label="Last Name" />
          <InputComponent name="email" type="text" label="Email" />

          <label>First Name</label>
          <Field name="firstname" type="text" autoComplete="off" />
          {touched.firstName && errors && <div>{errors.firstName}</div>}

          <label>Last Name</label>
          <Field name="lastname" type="text" autoComplete="off" />
          {touched.lastName && errors && <div>{errors.lastName}</div>}

          <label>Email</label>
          <Field name="email" type="text" autoComplete="off" />
          {touched.email && errors && <div>{errors.email}</div>}

          <button type="submit">Submit</button>
        </Form>
      )}
    </Formik>
    // <form onSubmit={handleSuccess}>
    //   <label for="firstname">First Name</label>
    //   <input name="firstname" type="text" required autoComplete="off" />

    //   <label for="lastname">Last Name</label>
    //   <input name="lastname" type="text" required autoComplete="off" />

    //   <label for="email">Email</label>
    //   <input name="email" type="text" required autoComplete="off" />

    //   <button type="submit">Submit</button>
    // </form>
  );
};

export default FormComponent;
