import React from "react";
import mapboxgl from "mapbox-gl";
import PopupContainer from "../PopupContainer";
import ReactDOM from "react-dom";
import "./../Pins.css";

const Landmark = ({ pin, map }) => {
  console.log();
  const coord = [pin.geometry.coordinates[0], pin.geometry.coordinates[1]];
  const popup = new mapboxgl.Popup();
  const popupNode = document.createElement("div");

  const handleClick = () => {
    ReactDOM.render(<PopupContainer pin={pin} />, popupNode);

    console.log("cloiked");

    popup.setLngLat(coord).setDOMContent(popupNode).addTo(map);
  };

  const handleMoveOff = () => {
    popup.remove();
  };

  return (
    <div
      id="pin-marker"
      onClick={handleClick}
      // onMouseLeave={handleMoveOff}
    >
      âšª
    </div>
  );
};

export default Landmark;
