import React, { useState } from "react";
import {
  ImageContainer,
  IMG,
  ButtonContainer,
  Button,
  ImgCounter,
  Caption,
} from "./styles";

const Slideshow = ({ images }) => {
  const [current, setCurrent] = useState(0);
  const length = images.length;

  React.useEffect(() => {
    setCurrent(0);
  }, [images]);

  const nextSlide = () => {
    setCurrent(current === length - 1 ? 0 : current + 1);
  };

  const prevSlide = () => {
    setCurrent(current === 0 ? length - 1 : current - 1);
  };

  const imgFile = "/assets/images/slide_" + images.file;

  if (!Array.isArray(images) || images.length <= 0) {
    return (
      <ImageContainer>
        <IMG src={imgFile} alt="landlines" className="image" />
        {images.caption ? <p>{images.caption}</p> : ""}
      </ImageContainer>
    );
  }

  return (
    <>
      <ImageContainer>
        {images.map((image, index) => {
          const imgSrc = "assets/images/slide_" + image.file;
          return (
            <>
              {index === current && (
                <IMG
                  src={imgSrc}
                  key={index}
                  alt="landlines"
                  className="image"
                />
              )}
            </>
          );
        })}
      </ImageContainer>

      {images.map((image, index) => {
        return (
          <>
            {index === current && (
              <Caption key={index}>{image.caption}</Caption>
            )}
          </>
        );
      })}

      <ButtonContainer>
        <Button onClick={() => prevSlide()}>Prev</Button>
        <ImgCounter>
          {current + 1}&thinsp;/&thinsp;{length}
        </ImgCounter>
        <Button onClick={() => nextSlide()}>Next</Button>
      </ButtonContainer>
    </>
  );
};

export default Slideshow;
