import { useState, useEffect } from 'react'
import { BottomSheet } from 'react-spring-bottom-sheet'

import 'react-spring-bottom-sheet/dist/style.css'
import './MobileSheet.css'

import ContentContainer from './ConetentContainer'

const MobileSheet = (props, {state, map, openState }) => {

    const [open, setOpen] = useState(true)

    // Ensure it animates in when loaded
    useEffect(() => {
      setOpen(true)
    }, [])

    function onDismiss() {
      setOpen(false)
    }
    return (
        <>
            <button className="bottom" onClick={() => setOpen(true)}>Open</button>
            <BottomSheet 
                open={open}
                blocking={false}
                onDismiss={onDismiss}
                snapPoints={({ maxHeight }) => [
                    maxHeight - maxHeight / 10,
                    maxHeight * 0.38,
                  ]}
            >
            {/* <ContentContainer state={state}/> */}

            </BottomSheet>  
        </>
    )
}

export default MobileSheet
