import React from "react";
import styled from "styled-components";
import ContentContainer from "./ConetentContainer";
import { FaTimes } from "react-icons/fa";

const Sidebar = (props, { state, map, onExit }) => {
  return (
    <div>
      <SidebarContainer>
        <button className="exit-button" onClick={props.onExit}>
          <FaTimes />
        </button>
        <ContentContainer state={props.state} />
      </SidebarContainer>
    </div>
  );
};

export default Sidebar;

const SidebarContainer = styled.section`
  @keyframes FadeIn {
    0% {
      opacity: 0;
    }

    100% {
      opacity: 1;
    }
  }
  width: 450px;
  height: calc(100vh - 10px);
  position: absolute;
  top: 5px;
  left: 5px;
  overflow: auto;
  opacity: 0;
  background-color: rgba(12, 12, 12, 0.95);
  opacity: 1;
  z-index: 111111;
  border-radius: 5px;
  box-shadow: 0 0 15px rgb(0 0 0 / 40%);
  color: #dadada;
  animation: FadeIn 1s ease-in forwards;

  /* overflow: hidden; */

  -ms-overflow-style: none; /* IE and Edge */
  scrollbar-width: none; /* Firefox */

  button.exit-button {
    background-color: transparent;
    border: none;
    color: #fff;
    position: absolute;
    top: 5px;
    right: 5px;
    width: 30px;
    height: 30px;
    cursor: pointer;
    z-index: 200;
  }
`;
