import React, { useState } from "react";

const Slideshow = ({ images }) => {
  const [current, setCurrent] = React.useState;
  const length = images.length;

  const nextSlide = () => {
    setCurrent(current === length - 1 ? 0 : current + 1);
  };

  const prevSlide = () => {
    setCurrent(current === length - 1 ? 0 : current - 1);
  };

  const imgFile = "/assets/images/slide_" + images.file;

  if (!Array.isArray(images) || images.length <= 0) {
    return (
      <>
        <div className="image-container">
          <img src={imgFile} alt="landlines" className="image" />
        </div>
        {/* <Modal images={images} open={isOpen} onClose={() => setIsOpen(false)} /> */}

        {/* <div className="caption">
              <p>
              Integer varius a ex a placerat. Orci varius natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.
              </p>
          </div> */}
      </>
    );
  }

  return (
    <section className="carousel">
      {images.map((image, index) => {
        const imgSrc = "assets/images/slide_" + image.file;

        return (
          <div
            className={
              index === current
                ? "carousel__item carousel__item--visible img-container"
                : "carousel__item img-container"
            }
            key={index}
          >
            {index === current && (
              <img src={imgSrc} alt="landlines" className="image" />
            )}
          </div>
        );
      })}

      {/* <div className="caption">
                    <p>
                    Integer varius a ex a placerat. Orci varius natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.
                    </p>
                </div> */}
      <div className="carousel__actions">
        <button id="carousel__actions--prev" onClick={prevSlide}>
          ⟵
        </button>
        <div className="counter">
          {current + 1}&thinsp;/&thinsp;{length}
        </div>
        <button id="carousel__actions--next" onClick={nextSlide}>
          ⟶
        </button>
      </div>
    </section>
  );
};

export default Slideshow;
