import React from "react";
import ReactDom from "react-dom";
import { ModalBG, Img } from "./styles";

const Modal = ({ open, images, current, onClose, prev, next }) => {
  if (!open) return null;

  if (!Array.isArray(images) || images.length <= 0) {
    const imgSrc = "assets/images/" + images.file;

    return ReactDom.createPortal(
      <ModalBG onClick={() => onClose()}>
        <Img src={imgSrc} alt="landlines" className="image" />
      </ModalBG>,
      document.getElementById("portal")
    );
  }

  return ReactDom.createPortal(
    <>
      {images.map((image, index) => {
        const imgSrc = "/assets/images/" + image.file;

        return (
          <ModalBG
            className={index === current ? "modal active" : ""}
            key={index}
            onClick={() => onClose()}
          >
            {index === current && (
              <Img src={imgSrc} alt="landlines" className="image" />
            )}
          </ModalBG>
        );
      })}
      <div className="modal_actions">
        <button id="carousel__actions--prev" onClick={prev}>
          ⟵
        </button>
        <div className="counter">
          {current + 1}&thinsp;/&thinsp;{images.length}
        </div>
        <button id="carousel__actions--next" onClick={next}>
          ⟶
        </button>
      </div>
    </>,
    document.getElementById("portal")
  );
};

export default Modal;
