import React from "react";
import styled from "styled-components";

const Header = () => {
  return (
    <>
      <HeaderMain id="header">
        <div className="open">
          <h1>Landlines</h1>
          <h2>M12 Studio</h2>
        </div>
      </HeaderMain>
    </>
  );
};

export default Header;

const HeaderMain = styled.div`
  position: absolute;
  top: 2rem;
  left: 2rem;
  z-index: 10000;
  text-transform: uppercase;
  line-height: 1;
  color: #fff;
  transition: all 0.6187s;
  pointer-events: none;
  width: 100%;

  h1 {
    font-size: clamp(1.5rem, 6vw, 10rem);
    letter-spacing: 1px;
    text-shadow: 0 0 15px rgba(0, 0, 0, 1);
  }

  h2 {
    font-size: clamp(1rem, 3vw, 6rem);
    font-weight: 400;
    text-shadow: 0 0 15px rgba(0, 0, 0, 1);
  }

  .open {
    color: red;
  }

  @media (max-width: 600px) {
    position: absolute;
    top: 0.5rem;
    left: 50%;
    transform: translateX(-50%);

    display: flex;
    justify-content: center;
    h1,
    h2 {
      font-size: 1rem;
    }

    h2 {
      margin-left: 1rem;
    }
  }
`;
