import './Modal.css'
import ReactDom from 'react-dom'


const Modal = ({open, images, current, onClose, prev, next}) => {

    if(!open) return null    

    if(!Array.isArray(images) || images.length <= 0){
        const imgSrc = '/assets/images/' + images

        return ReactDom.createPortal(
            <div className="modal active" onClick={() => onClose()}>
                <img src={imgSrc} alt="landlines" className='image'/>
            </div>,
            document.getElementById('portal')
        )};


    return ReactDom.createPortal(

        <div >

            {images.map((image, index) => {

            const imgSrc = '/assets/images/' + image

            return (
                <div className={ index === current ? 'modal active' : ''} 
                key={index}
                onClick={() => onClose()}
                >
                    {index === current && (<img src={imgSrc} alt="landlines" className='image'/>)}
                </div>
            )
            })}
                <div className="modal_actions">
                    <button id="carousel__actions--prev" onClick={prev}  >⟵</button>
                    <div className="counter">{current + 1}&thinsp;/&thinsp;{images.length}</div>
                    <button id="carousel__actions--next" onClick={next} >⟶</button>
                </div>
        </div>,
        document.getElementById('portal')
    )
}

export default Modal
