import styled from "styled-components";

export const ModalBG = styled.div`
  position: absolute;
  background: rgba(0, 0, 0, 0.95);
  top: 0px;
  left: 0px;
  height: 100%;
  width: 100%;
  color: #fff;
  pointer-events: all;
  z-index: 1000;
  display: grid;
  place-items: center;
  border-radius: 5px;
`;

export const Img = styled.img`
  max-width: 70vw;
  max-height: 70vh;
  position: absolute;
  width: auto;
  height: auto;
  z-index: 1000;
  pointer-events: none;
`;
