import styled from "styled-components";

export const Container = styled.section`
  position: fixed;
  bottom: 0;
  left: 0;
  min-height: 80vh;
  width: 100vw;
  z-index: -1;
  background: #121212;

  color: #fff;
  display: flex;
  flex-direction: column;
  justify-content: center;

  max-width: 400px;
  margin: 7.5vw;
  pointer-events: all;

  h2 {
    text-transform: uppercase;
  }
`;
