import React, { useRef, useEffect } from "react";
import mapboxgl from "mapbox-gl";
import ReactDOM from "react-dom";
import PopupContainer from "../PopupContainer";

import LandmarksData from "../../data/map.json";

const LandmarksComponent = ({ map }) => {
  const popUpRef = useRef(new mapboxgl.Popup({ offset: 10 }));

  if (map) {
    map.on("click", "landmarks", (e) => {
      const features = map.queryRenderedFeatures(e.point, {
        layers: ["landmarks"],
      });
      popUpRef.current.remove();

      if (features.length > 0) {
        const feature = features[0];

        console.log(popUpRef.current);
        // create popup node
        const popupNode = document.createElement("div");
        ReactDOM.render(<PopupContainer id="place-pin" />, popupNode);
        popUpRef.current
          .setLngLat(feature.geometry.coordinates)
          .setDOMContent(popupNode)
          .addTo(map);
      }
    });

    // Change the cursor to a pointer when the it enters a feature in the 'Landmarks' layer.
    map.on("mouseenter", "landmarks", () => {
      map.getCanvas().style.cursor = "pointer";
    });

    // Change it back to a pointer when it leaves.
    map.on("mouseleave", "landmarks", () => {
      map.getCanvas().style.cursor = "";
    });
  }

  return <div></div>;
};

export default LandmarksComponent;
