import styled from "styled-components";

export const Container = styled.section`
  position: fixed;
  bottom: 0;
  left: 0;
  height: 80vh;
  max-width: 100%;

  z-index: -1;
  color: pink;
  display: flex;
  flex-direction: column;
  justify-content: center;

  h2 {
    text-transform: uppercase;
  }
`;
