import React, { useEffect, useRef } from "react";
import mapboxgl from "mapbox-gl";
import ReactDOM from "react-dom";
import LandmarkData from "../../data/map.json";
// import Pins from "../Pins";

import PopupContainer from "../PopupContainer";

const Landmarks = ({ map }) => {
  const popUpRef = useRef(new mapboxgl.Popup({ offset: 10 }));

  useEffect(() => {
    if (map) {
      LandmarkData.features.forEach((landmark, index) => {
        // create React reference
        const ref = React.createRef();
        // Create a new DOM node and save it to the React Ref
        ref.current = document.createElement("div");

        ref.current.className += "pins-markers";

        const handleClick = () => {
          const popup = new mapboxgl.Popup();

          // const popupNode = document.createElement("div");
          // ReactDOM.render(<PopupContainer id="place-pin" />, popupNode);
          // popUpRef.current
          //   .setLngLat(landmark.geometry.coordinates)
          //   .setDOMContent(popupNode)
          //   .addTo(map);

          // console.log("testt");
        };

        // render a Marker Component on our new DOM nodelib
        ReactDOM.render(<div onClick={handleClick}>TTT</div>, ref.current);

        new mapboxgl.Marker(ref.current)
          .setLngLat(landmark.geometry.coordinates)
          .addTo(map);
      });

      console.log("ehlo");
    }
  }, [map]); // eslint-disable-line react-hooks/exhaustive-deps
  return <div></div>;
};

export default Landmarks;
