import React from 'react'
import styled from 'styled-components'
import { Slideshow } from './Slideshow'

const ConetentContainer = (props) => {



    return (
        <>
        <Header>
        <Slideshow 
            images={props.state.images} 
            alt={props.state.title}
                />
            <div className="project-container">
            <h2 className="project-title">{props.state.title}</h2>
                    <h3 className="project-author">{props.state.artist}</h3>
                    <div className="project-description-container">
                            <div className="project-description" dangerouslySetInnerHTML={{__html: props.state.description}}>

                        </div>
                    </div>
            </div>
        </Header>
         </>
    )
}

export default ConetentContainer

const Header = styled.header`
    /* padding: 1rem; */

    img {
        width: 100%;
    }

    .project-container{
    padding: 20px 40px;
    }

.project-container>*{
    margin-bottom: 1em;
    color: #dadada;
    overflow: auto;
    }

.project-container h2{
    font-size: 2em;
    font-weight: 500;
    line-height: 1.2;
}

.project-container h3{
    font-size: 1em;
    text-transform: uppercase;
}

.project-description{
    font-size: 1rem;
    line-height: 1.5;
    color: #dadada;
    letter-spacing: .2px;    
}

.project-hero-image{
    position: relative;
    display: block;
    width: 100%;
    padding-bottom: 66.66%;
}

.project-hero-image img{
    position: absolute;
    top: 0;
    width: 100%;
    object-fit: cover;
    object-position: center;
}

`
