import React, { useRef, useEffect } from "react";
import mapboxgl from "mapbox-gl";
import ReactDOM from "react-dom";
import PopupContainer from "../PopupContainer";

import LandmarksData from "../../data/map.json";

const LandmarksComponent = ({ map }) => {
  const popUpRef = useRef(new mapboxgl.Popup({ offset: 10 }));

  if (map) {
  }

  useEffect(() => {
    if (map) {
      LandmarksData.features.forEach((pin, index) => {
        // create React reference
        const ref = React.createRef();
        // Create a new DOM node and save it to the React Ref
        ref.current = document.createElement("div");
        // render a Marker Component on our new DOM nodelib
        ReactDOM.render(<div className="pins-marker" />, ref.current);

        new mapboxgl.Marker(ref.current)
          .setLngLat(pin.geometry.coordinates)
          .addTo(map);
      });
    }
  });

  return <div></div>;
};

export default LandmarksComponent;
