import React, { useState } from 'react'
import './Slideshow.css'
import Modal from './Modal'


export const Slideshow = ({ images, map }) => {

    // Open Modal State
    const [isOpen, setIsOpen] = useState(false)


    React.useEffect(() => {setCurrent(0)},[images])
    const [current, setCurrent] = useState(0)
    const length = images.length


    const nextSlide = () => {
        setCurrent(current === length - 1 ? 0 : current + 1)
    }

    const prevSlide = () => {
        setCurrent(current === 0 ? length - 1 : current - 1)
    }

    const imgSrc = 'http://richardsaxton.org/websites/m12_landlines/slide_' + images


    if(!Array.isArray(images) || images.length <= 0){
        return (
            <>
                <div className="image-container" onClick={() => setIsOpen(true)}>
                    <img src={imgSrc} alt="landlines" className="image"/>
                </div>
                <Modal
                images={images}
                open={isOpen}
                onClose={() => setIsOpen(false)}/>

                {/* <div className="caption">
                    <p>
                    Integer varius a ex a placerat. Orci varius natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.
                    </p>
                </div> */}

            </>
        )
    };

    return (
            <section className='carousel'>
                {images.map((image, index) => {

                    const imgSrc = 'assets/images/slide_' + image

                    return (
                        <div className={ index === current ? 'carousel__item carousel__item--visible img-container' : 'carousel__item img-container'} 
                        key={index}
                        onClick={() => setIsOpen(true)}
                        >
                            {index === current && (<img src={imgSrc} alt="landlines" className='image'/>)}

                        </div>

                    )
                })}

                <Modal
                    open={isOpen}
                    current={current}
                    images={images}
                    onClose={() => setIsOpen(false)}
                    prev={prevSlide}
                    next={nextSlide}
                />
                {/* <div className="caption">
                    <p>
                    Integer varius a ex a placerat. Orci varius natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.
                    </p>
                </div> */}
                <div className="carousel__actions">
                    <button id="carousel__actions--prev" onClick={prevSlide}>⟵</button>
                    <div className="counter">{current + 1}&thinsp;/&thinsp;{length}</div>
                    <button id="carousel__actions--next" onClick={nextSlide}>⟶</button>
                </div>
            </section>

    )
}
