import React,{ useState } from 'react'
import mapboxgl from 'mapbox-gl'

import './Markers.css'

// import ProjectInfoComponent from './ProjectInfoComponent'
// import Sidebar from './Sidebar'
import MobileSheet from './MobileSheet'
import projects from '../data/landlines.json'

const Markers = ({map}) => {
    const [open, setOpen] = useState(false)

    const [state, setstate] = useState({
        title: '', 
        artist: '', 
        description: '', 
        images: '', 
        id: '', 
        alt: ''
    })


    function addMarkers() {
        projects.features.forEach(function(marker, index) {

            const el = document.createElement('div');
            el.id = "marker-" + marker.properties.id;
            el.className = 'marker' + index;    
            el.innerHTML = `<div class="marker"><h4><span>` + marker.properties.alt + `</span></h4></div>` ;

            new mapboxgl.Marker(el)
            .setLngLat(marker.geometry.coordinates)
            .addTo(map);

            el.addEventListener('click', function(e){
            flyToProject(marker)

            })
        });
        }

        const windowHeight = window.innerHeight

        const mapPaddingMobile = `bottom:` + windowHeight * 0.38

        const mapPaddingDesktop = 'left: 500 '




    // Use Mapbox GL JS's `flyTo` to move the camera smoothly
    // a given center point.
    function flyToProject(currentFeature) {
        setOpen(true)

        map.flyTo({
            center: [currentFeature.geometry.coordinates[0], currentFeature.geometry.coordinates[1]],
            zoom: 12,
            speed: 1,
            padding: windowHeight > 450 ? {mapPaddingDesktop} : {mapPaddingMobile},
        });

        setstate(currentFeature.properties)
    }


        if(map){
            map.on('load', () => {
                addMarkers()
            });
        }

        const onDismiss = () => {
            setOpen(false)
            map.flyTo({
                center: [ -106.1828, 37.4792 ],
                zoom: 7.5,
                speed: 1,
                padding: { bottom: 0 }, 
            });

        }

        if(window.innerWidth > 450 ) return null
        else return <MobileSheet state={state} map={map} openState={open} setData={setOpen} onDismiss={onDismiss}/>
}

export default Markers
