import styled from "styled-components";

export const HeaderContainer = styled.header`
  position: absolute;
  top: 2rem;
  left: 2rem;
  z-index: 4;
  text-transform: uppercase;
  line-height: 1;
  color: #fff;
  transition: all 0.6187s;
  /* pointer-events: none; */
  width: 100%;

  pointer-events: none h1, h2 {
    text-shadow: 0 0 15px rgba(0, 0, 0, 1);
    cursor: pointer;
  }
`;

export const H1 = styled.h1`
  font-size: clamp(1.5rem, 6vw, 10rem);
  letter-spacing: 1px;
  cursor: pointer;
`;

export const H2 = styled.h2`
  font-size: clamp(1rem, 3vw, 6rem);
  font-weight: 400;
`;
