import React,{ useState } from 'react'
import mapboxgl from 'mapbox-gl'

import './Markers.css'

import MobileSheet from './MobileSheet'
import Sidebar from './Sidebar'


import projects from '../data/landlines.json'




const Markers = ({map}) => {
    const [open, setOpen] = useState(false)

    const [state, setstate] = useState({
        title: '', 
        artist: '', 
        description: '', 
        images: '', 
        id: '', 
        alt: ''
    })



    function addMarkers() {
        projects.features.forEach(function(marker, index) {

            const el = document.createElement('div');
            el.id = "marker-" + marker.properties.id;
            el.className = 'marker' + index;    
            el.innerHTML = `<div class="marker"><h4><span>` + marker.properties.alt + `</span></h4></div>` ;

            new mapboxgl.Marker(el)
            .setLngLat(marker.geometry.coordinates)
            .addTo(map);

            el.addEventListener('click', function(e){
            flyToProject(marker)
            // setOpen(true)

            })
        });
        }

        const mapPadding = window.innerHeight 

        console.log(mapPadding)


    // Use Mapbox GL JS's `flyTo` to move the camera smoothly
    // a given center point.
    function flyToProject(currentFeature) {
        map.flyTo({
            center: [currentFeature.geometry.coordinates[0], currentFeature.geometry.coordinates[1]],
            zoom: 12,
            speed: 1,
            // padding: { left: 500}, //Push center to the left 500px,
        });

        setstate(currentFeature.properties)


        // Open Sidebar
        // const sidebar = document.querySelector('#sidebar')
        // sidebar.classList.add("open");

        // const header = document.querySelector('.header')
        // header.classList.add("slide");
        // header.classList.add("visible")

        // const headerStart = document.querySelector('.header-start')
        // headerStart.classList.add("hidden");

    }

        console.log(state)

        if(map){
            map.on('load', () => {
                addMarkers()
            });
        }

    return (
        <>  
            <Sidebar />
            <MobileSheet
            // open={open} 
            state={state} />
        </>
    )
}

export default Markers
