import React, {useEffect} from 'react'
import mapboxgl from "mapbox-gl";
import ReactDOM from 'react-dom'
import styled from 'styled-components'
import ProjectMarker from './ProjectMarker'

const ProjectComponent = ({map, projects}) => {
    useEffect(() => {
        if(map){
            projects.features.forEach((project, index) => {
                // create React reference
                const ref = React.createRef();
                // Create a new DOM node and save it to the React Ref
                ref.current = document.createElement('div');
                // render a Marker Component on our new DOM nodelib
                ReactDOM.render(<ProjectMarker project={project} />, ref.current);

                console.log(project)
                new mapboxgl.Marker(ref.current)
                    .setLngLat(project.geometry.coordinates)
                    .addTo(map)

                // reactDOM.render(<Marker/>, el)
            })
        }   
    }, [map]); // eslint-disable-line react-hooks/exhaustive-deps


    return (
        <div>

        </div>
    )
}


export default ProjectComponent

const MarkerComponent = styled.div`
background-color: black;
display: block !important;
height: 10px;
width: 20px;
cursor: pointer;
`
