import React, { useState } from "react";
import {
  ImageContainer,
  IMG,
  Carousel,
  ButtonContainer,
  NextButton,
  PrevButton,
  ImgCounter,
} from "./styles";

const Slideshow = ({ images }) => {
  const [current, setCurrent] = useState(0);
  const length = images.length;

  console.log(length);

  React.useEffect(() => {
    setCurrent(0);
  }, [images]);

  const nextSlide = () => {
    setCurrent(current === length - 1 ? 0 : current + 1);
  };

  const prevSlide = () => {
    setCurrent(current === 0 ? length - 1 : current - 1);
  };

  const imgFile = "/assets/images/slide_" + images.file;

  if (!Array.isArray(images) || images.length <= 0) {
    return (
      <>
        <ImageContainer>
          <IMG src={imgFile} alt="landlines" className="image" />
        </ImageContainer>
      </>
    );
  }

  return (
    <Carousel>
      {images.map((image, index) => {
        const imgSrc = "assets/images/slide_" + image.file;

        return (
          <ImageContainer>
            {index === current && (
              <IMG src={imgSrc} alt="landlines" className="image" />
            )}
          </ImageContainer>
        );
      })}

      <ButtonContainer>
        <PrevButton onClick={() => nextSlide()}>Prev</PrevButton>
        <ImgCounter></ImgCounter>
        <NextButton onClick={() => nextSlide()}>Next</NextButton>
      </ButtonContainer>
    </Carousel>
  );
};

export default Slideshow;
