import styled from "styled-components";

export const Page = styled.div`
  margin-bottom: 80vh;
  background: #fff;
`;

export const Intro = styled.div`
  height: 100vh;
  width: 100vw;
  background-color: #2c9da5;
  z-index: -3;
`;

export const Back = styled.div`
  position: fixed;
  bottom: 0;
  left: 0;
  height: 80vh;
  z-index: -1;

  color: pink;
  display: flex;
  flex-direction: column;
  justify-content: center;
`;
