import React from "react";
import Pins from "../../data/map.json";
import mapboxgl from "mapbox-gl";
import ReactDOM from "react-dom";
import Landmark from "../Landmark";

const Landmarks = ({ map }) => {
  const handleClick = () => {
    console.log("click");
  };

  if (map) {
    Pins.features.forEach((pin, index) => {
      const ref = React.createRef;
      ref.current = document.createElement("div");
      ref.current.id = "pin-marker";

      ReactDOM.render(<Landmark />, ref.current);

      new mapboxgl.Marker(ref.current)
        .setLngLat(pin.geometry.coordinates)
        .addTo(map);
      // const coord = [pin.geometry.coordinates[0], pin.geometry.coordinates[1]];
      // var el = document.createElement("div");
      // el.id = "pin-marker";

      // const marker = new mapboxgl.Marker(el);
      // marker.setLngLat(coord);
      // marker.addTo(map);
    });
  }

  return <div></div>;
};

export default Landmarks;
